from cata
where cata_id = #{cataId,jdbcType=INTEGER}

select cata_id, cata_level, cata_name, sub_cata_count, `sequence`, `enable`, parent_cata
 from cata where 1=1

   and enable = #{enable,jdbcType=CHAR}


   and cata_name = #{cataName,jdbcType=VARCHAR}

